import React from 'react'
import { BrowserRouter as Router, Route } from 'react-router-dom'
// 1. Dependencies
// 2. Components
// 3. for style
import styled from 'styled-components'

// 4. Static Resources

//——*——*——*——*——*——//

const PResearch = props => {
  const { title, desc, imgs_odd, imgs_even } = props
  return (
    <StyledPResearch>
      <div className="textWrapper">
        <h1>{title}</h1>
        <p>{desc}</p>
      </div>
      <div className="animWrapper">
        <div className="sect_odd">
          {imgs_odd.map(item => {
            const { research_odd } = item
            return (
              <div className="itemWrapper">
                <img
                  src={require(`../img/${item.imgSrc}.jpg`)}
                  alt={item.desc}
                />
                <p>{item.desc}</p>
              </div>
            )
          })}
        </div>
        <div className="sect_even">
          {imgs_even.map(item => {
            const { research_even } = item
            return (
              <div className="itemWrapper">
                <img
                  src={require(`../img/${item.imgSrc}.jpg`)}
                  alt={item.desc}
                />
                <p>{item.desc}</p>
              </div>
            )
          })}
        </div>
        <div className="sect_odd_last">
          {imgs_odd.map(item => {
            const { research_odd } = item
            return (
              <div className="itemWrapper">
                <img
                  src={require(`../img/${item.imgSrc}.jpg`)}
                  alt={item.desc}
                />
                <p>{item.desc}</p>
              </div>
            )
          })}
        </div>
        <div className="sect_even_last">
          {imgs_even.map(item => {
            const { research_even } = item
            return (
              <div className="itemWrapper">
                <img
                  src={require(`../img/${item.imgSrc}.jpg`)}
                  alt={item.desc}
                />
                <p>{item.desc}</p>
              </div>
            )
          })}
        </div>
      </div>
    </StyledPResearch>
  )
}

const StyledPResearch = styled.div`
